

function AppObject(name, link, image, c1, c2, rating) {
	this.name = name;
	this.link = link;
	this.image = image;
	this.c1 = c1;
	this.c2 = c2;
	this.rating = rating;
	
	this.getName = function() {
		return this.name;
	};
	
	this.getLink = function() {
		return this.link;
	};

	this.getImage = function() {
		return this.image;
	};
	
	this.getC1 = function() {
		return this.c1;
	};
	
	this.getC2 = function() {
		return this.c2;
	};
	
	this.getRating = function() {
		return this.rating;
	};
	
	this.setName = function(name) {
		this.name = name;
	};
	
	this.setLink = function(link) {
		this.link = link;
	};
	
	this.setImage = function(src) {
		this.image = src;
	};
	
	this.setC1 = function(c1) {
		this.c1 = c1;
	};
	
	this.setC2 = function(c2) {
		this.c2 = c2;
	};
	
	this.setRating = function(rating) {
		this.rating = rating;
	};
}

module.exports = AppObject;