
function Category(name, color) {
	this.name = name;
	this.color = color;
	
	this.getName = function() {
		return this.name;
	};
	
	this.getColor = function() {
		return this.color;
	};
	
	this.setName = function(newName) {
		this.name = newName;
	};
	
	this.setColor = function(newColor) {
		this.color = newColor;
	};
}

module.exports = Category;